mapper.select:
SELECT QNA_BOARD_NUM
FROM BOOK_MARK
WHERE MEMBER_ID = #{memberId}

mapper.insert:
INSERT INTO BOOK_MARK (
BOOK_MARK_NUM
, QNA_BOARD_NUM
, MEMBER_ID
) VALUES (
(SELECT NVL(MAX(BOOK_MARK_NUM),0) +1 FROM BOOK_MARK)
, #{qnaBoardNum}
, #{memberId}
)

mapper.delete:
DELETE BOOK_MARK
WHERE QNA_BOARD_NUM = #{qnaBoardNum}
AND MEMBER_ID = #{memberId}
