mapper.insert:
INSERT INTO FREE_BOARD(
    BOARD_NUM
    , TITLE
    , CONTENT
    , WRITER
) VALUES(
    (SELECT NVL(MAX(BOARD_NUM),0) +1 FROM FREE_BOARD)
    , #{title}
    , #{content}
    , #{writer}
)

mapper.update:
UPDATE FREE_BOARD
SET
TITLE=#{title}
,CONTENT=#{content}
WHERE BOARD_NUM=#{boardNum}

mapper.insert:
INSERT INTO BOARD_REPLY(
    REPLY_NUM
    , REPLY_TEXT
    , REPLYER
    , BOARD_NUM
)
VALUES(
    (SELECT NVL(MAX(REPLY_NUM),0) +1 FROM BOARD_REPLY)
    ,#{replyText}
    ,#{replyer}
    ,#{boardNum}
)
