mapper.select:
SELECT BOARD_NUM
FROM FREE_BOOK_MARK
WHERE MEMBER_ID = #{memberId}

mapper.insert:
INSERT INTO FREE_BOOK_MARK (
    FREE_BOOK_MARK_NUM
    , BOARD_NUM
    , MEMBER_ID
) VALUES (
    (SELECT NVL(MAX(FREE_BOOK_MARK_NUM),0) +1 FROM FREE_BOOK_MARK)
    , #{boardNum}
    , #{memberId}
)

mapper.delete:
DELETE FREE_BOOK_MARK
WHERE BOARD_NUM = #{boardNum}
AND MEMBER_ID = #{memberId}
