mapper.insert:
INSERT INTO QNA_BOARD (
    QNA_BOARD_NUM
    , QNA_BOARD_TITLE
    , QNA_BOARD_CONTENT
    , QNA_BOARD_WRITER

        , ORIGIN_FILE_NAME
        , ATTACHED_FILE_NAME

) VALUES (
    (SELECT NVL(MAX(QNA_BOARD_NUM),0) +1 FROM QNA_BOARD)
    , #{qnaBoardTitle}
    , #{qnaBoardContent}
    , #{qnaBoardWriter}

        , #{originFileName}
        , #{attachedFileName}

)

mapper.update:
UPDATE QNA_BOARD
SET QNA_READ_CNT = QNA_READ_CNT + 1
WHERE QNA_BOARD_NUM = #{qnaBoardNum}

UPDATE QNA_BOARD
SET QNA_BOARD_TITLE = #{qnaBoardTitle}
    , QNA_BOARD_CONTENT = #{qnaBoardContent}

        , ORIGIN_FILE_NAME = #{originFileName}
        , ATTACHED_FILE_NAME = #{attachedFileName}

WHERE QNA_BOARD_NUM = #{qnaBoardNum}
