mapper.select:
SELECT QNA_BOARD_NUM
FROM QNA_BOOK_MARK
WHERE MEMBER_ID = #{memberId}

mapper.insert:
INSERT INTO QNA_BOOK_MARK (
QNA_BOOK_MARK_NUM
, QNA_BOARD_NUM
, MEMBER_ID
) VALUES (
(SELECT NVL(MAX(QNA_BOOK_MARK_NUM),0) +1 FROM QNA_BOOK_MARK)
, #{qnaBoardNum}
, #{memberId}
)

mapper.delete:
DELETE QNA_BOOK_MARK
WHERE QNA_BOARD_NUM = #{qnaBoardNum}
AND MEMBER_ID = #{memberId}
